import itertools


def find_marker(line: str, length = 4):
    character_in_line = set([c for c in line])
    permutations = itertools.permutations(character_in_line, length)
    permutations = ["".join(p) for p in permutations]
    find = []
    for p in permutations:
        f = line.find(p)
        if f > - 1:
            find.append(f)
    first_occurence = min(find)
    return first_occurence + length

def find_big_marker(line, length = 14):
    for i in range(0, len(line) - length):
        chars = [a for a in line[i: i + length]]
        if len(set(chars)) == length:
            return i + length

if __name__ == "__main__":
    entry = """"""
    print(find_marker(entry, 4))
    print(find_big_marker(entry, 14))